class CatalogueModel {
  static List<Item> items = [
    // Item(
    //   id: 001,
    //   name: "Iphone 7",
    //   desc: "Apple i phone mazing",
    //   price: 900,
    //   color: "#3305a",
    //   image:
    //       "data:image/jpeg;base64,",
    // )
  ];

  Item getById(int id) =>
      items.firstWhere((element) => element.id, orElse: null);

  static Item getByPosition(int pos) => items[pos];
}

class Item {
  final id;
  final name;
  final desc;
  final price;
  final color;
  final image;

  Item({this.id, this.name, this.desc, this.price, this.color, this.image});

  factory Item.fromMap(Map<String, dynamic> map) {
    return Item(
      id: map["id"],
      name: map["name"],
      desc: map["desc"],
      price: map["price"],
      color: map["color"],
      image: map["image"],
    );
  }

  toMap() => {
        "id": id,
        "name": name,
        "desc": desc,
        "price": price,
        "color": color,
        "image": image,
      };
}
